      return (
        <>
        <p>ボタンAを{val1}回押しました！</p>
        <button onClick = {clickHandler('a')}>ボタンA</button> 
        
        <p>ボタンBを{val2}回押しました！</p>
        <button onClick = {clickHandler}>ボタンB</button> 
        
