import { useState } from "react";

const Example = () => {
    let [ val, setVal ] = useState(0);
    const clickHandler = () => {
        console.log(val)
        return setVal(val+1);
      }
      return (
        <>
        <p>ボタンAを{setVal}回押しました！</p>
        <button onClick = {clickHandler}>ボタンA</button>  
        </>
      )
};

export default Example;
