import { useState } from 'react';

const Example = () => {
  const numArray = [1, 2, 3, 4, 5];
  const newNumArray = useState(numArray);
  const shuffle = () => {
   
    newNumArray([...numArray,numArray[0]+1]);
  };
  return (
    <>
       <h1>{numArray}</h1>
       <button onClick={shuffle}>shuffle</button>
    </>
  );
};

export default Example;
