import { useState } from "react";

const Example = () => {
    let [ val1, setVal1 ] = useState(0);
    let [ val2, setVal2 ] = useState(0);
    let [ val3, setVal3 ] = useState(0);
    const clickHandler = () => {
        console.log(val1)
        return setVal1(val1+1);
      }
      return (
        <>
        <p>ボタンAを{val1}回押しました！</p>
        <button onClick = {clickHandler}>ボタンA</button> 
        
        <p>ボタンBを{val2}回押しました！</p>
        <button onClick = {clickHandlerB}>ボタンB</button> 
        
        <p>ボタンCを{val3}回押しました！</p>
        <button onClick = {clickHandlerC}>ボタンC</button> 
        </>
      )
};

export default Example;
