import {useState} from "react";

const Example = () => {
  const [fruit, setFruit] = useState("Aplle") 
  const onChange = (e) => setFruit(e.target.value)

  return (
    <>
    <p style={{ textAlign: "center" }}>
      startフォルダの内容が表示されます。
      <br />
      練習用に使ってください！
    </p>
    <lavbel>
      <input
        type = "radio"
        value = "Apple"
        checked = {fruit === "Apple" }
        onChange ={onChange}
      />
      Apple
    </lavbel>
    <lavbel>
      <input
        type = "radio"
        value = "Banana"
        checked = {fruit === "Banana"}
        onChange ={onChange}
      />
      Banana
    </lavbel>
    <lavbel>
      <input
        type = "radio"
        value = "Cherry"
        checked = {fruit === "Cherry"}
        onChange ={onChange}
      />
      Cherry
    </lavbel>
    <h3>わたしは{fruit}が食べたい</h3>
    </>
  );
};

export default Example;
