import {useState} from "react";

const Example = () => {

  const todosList = [{
    id: 1,
    content: "店予約する",
  },
  {
    id: 2,
    content: "卵買う",
  },
  {
    id: 3,
    content: "郵便出す",
  },
];

const [task, setTask] = useState("");

const onClick = (e) => {
  todosList.map(tasks => {
    const newTask = {...todosList};
    console.log(e.target.value);
    if( newTask.id != e.target.value){
      newTask.add(tasks);
    }
    return setTask(newTask);
})
}
  return (
    <>
      <h3>完成系と同じような機能を作成してください。</h3>
      <p>また、枠線で囲まれた部品をコンポーネントに分離してください。</p>
      <h2>Reminder</h2>
      {
      todosList.map(({id, content})=>{
        return(
          <div>
          <label key ={id}></label>
          <button 
            value =  {id}
            htmlFor = {id}
            onClick={onClick}
          >完了
          </button>
          {content}
          </div>
          )
      }) 
      }
     
    </>
  );
};

export default Example;
