import {useState} from "react";
import Todo from "./components/Todo.js";
import List from "./components/List.js";
import Form from "./components/Form.js";

const Example = () => {


const [task, setTask] = useState("");

const onClick = (e) => {
  todosList.map(tasks => {
    const newTask = {...todosList};
    console.log(e.target.value);
    if( newTask.id != e.target.value){
      newTask(tasks);
    }
    return setTask(newTask);
})
}
  return (
    <>
      <h3>完成系と同じような機能を作成してください。</h3>
      <p>また、枠線で囲まれた部品をコンポーネントに分離してください。</p>
      <h2>Reminder</h2>
      
      <Todo 
      {...todosList.map(({id, content})=>{
        return(
          <div>
          <label key ={id}></label>
          <button 
            value =  {id}
            htmlFor = {id}
            onClick={onClick}
          >完了
          </button>
          {content}
          </div>
          )
      }) 
    }
      />
       <input
        id="123"
        placeholder=""  //何も入力されていないときに表示される文字を指定できる
        value={task}
        onChange={(e)=>setTask(e.target.value)}
      />
      <button >追加</button>

     
    </>
  );
};

export default Example;
