const Example = () => {

  // 関数型
  // （値の）状態と処理を分離して管理
  // A(data) -> B(data) -> C(data) -> 結果
  // ★ 状態と処理は切り離す
  const nums = [1,2,3];
  const sum = (arry) => {
    const nums = arry;
    // let sumValue = 0;
    // for(let i = 0 ; i < nums.length ; i++){
    //   sumValue += nums[i];
    // }
    // nums.forEach(num => sumValue += num);
    let sumValue = nums.reduce((sumValue,nums) => sumValue + nums)
    return sumValue;
  }


  // オブジェクト指向型
  // 状態（データ）と処理を対で管理
  // obj.method(); -> 結果
  const numObj = {
    nums:[1,2,3],   // 状態（データ）
    sum(){          // 処理
      const nums = this.nums;
      let sumValue = 0;
      for(let i = 0 ; i < nums.length ; i++){
        sumValue += nums[i];
      }
      return sumValue;
    }
  }


  return (
    <>
      <h3>状態管理と処理を分離</h3>
      <p>状態（データ）と処理（やりたいこと）は切り離す</p>
      <div>関数型：{sum(nums)}</div>
      <div>オブジェクト指向型：{numObj.sum()}</div>
    </>
  );
};

export default Example;
