const Example = () => {


  // オブジェクト指向型
  // 状態（データ）と処理を対で管理
  // obj.method(); -> 結果
  const numObj = {
    nums:[1,2,3],
    sum(){
      const nums = this.nums;
      let sumValue = 0;
      for(let i = 0 ; i < nums.length ; i++){
        sumValue += nums[i];
      }
      return sumValue;
    }
  }


  return (
    <>
      <h3>状態管理と処理を分離</h3>
      <p>状態（データ）と処理（やりたいこと）は切り離す</p>
    </>
  );
};

export default Example;
