const Example = () => {

  // 

  // オブジェクト指向型
  // 状態（データ）と処理を対で管理
  // obj.method(); -> 結果
  const numObj = {
    nums:[1,2,3],   // 状態（データ）
    sum(){          // 処理
      const nums = this.nums;
      let sumValue = 0;
      for(let i = 0 ; i < nums.length ; i++){
        sumValue += nums[i];
      }
      return sumValue;
    }
  }


  return (
    <>
      <h3>状態管理と処理を分離</h3>
      <p>状態（データ）と処理（やりたいこと）は切り離す</p>
      <div>オブジェクト指向型：{numObj.sum()}</div>
      <div>関数型：</div>
    </>
  );
};

export default Example;
