import { createContext, useReducer, useContext } from "react";

const TodoContext = createContext();
const TodoDispatchContext = createContext();

const reducer = (state)=>{
    return { ...state};
}

const TodoProvider = ({children}) => {

    const todosList = [
        {
          id: 1,
          content: "店予約する",
        },
        {
          id: 2,
          content: "卵買う",
        },
        {
          id: 3,
          content: "郵便出す",
        },
      ];

    const [ todos, setTodos ] = useState(todosList);
    
    const deleteTodo = (id) => {
      const newTodos = state.filter((todo) => {
        return todo.id !== id;
      });
  
      dispatch(newTodos);
    }
  
    const createTodo = (todo) => {
      dispatch([...state, todo]);
    }
  
    const upDateTodo = (todo) => {
      const newTodo =  todosList.map(_todo => { 
        return _todo.id === todo.id ? {..._todo, ...todo } : {..._todo}
     });
     setTodos(newTodo);
    }
    
    const [state, dispatch] = useReducer(reducer, todosList);

    return(
        <TodoContext.Provider value={state}>
            <TodoDispatchContext.Provider value={dispatch}>
                {children}
            </TodoDispatchContext.Provider>
        </TodoContext.Provider>
    )
}

const useTodos = () => useContext(TodoContext);
const useDispatchTodos = () => useContext(TodoDispatchContext);

export { TodoProvider, useTodos, useDispatchTodos }
