import { createContext, useReducer, useContext } from "react";
import Counter from "../components/Counter";
export const CounterContext = createContext();
export const CounterDispatchContext = createContext();

const CounterProvider = ({children}) => {
     const [state, dispatch] = useReducer((prev, { type, step }) => {
      switch (type) {
        case "+":
          return prev + step;
        case "-":
          return prev - step;
        default:
          throw new Error('不明なactionです。')
      }
    }, 0);

    const countUp = () => {
        dispatch({ type: "+", step: 2 });
      };
      const countDown = () => {
        dispatch({ type: "-", step: 2 });
      };

    return(
        <CounterContext.Provider value={state}>
            <CounterDispatchContext value={dispatch}>
                {children}
            </CounterDispatchContext>
        </CounterContext.Provider>
    )
}
const useCounter = () => useContext(CounterContext);
const useCounterDispatch = () => useContext(CounterDispatchContext);

export { CounterProvider, useCounter, useCounterDispatch }
