import {TodoProvider, useTodo, useTodoDispatch } from "../context/TodoContext";
import List from "./List"
import Form from "./Form"

const Todo = () => {

  const state = useTodo();
  const dispatch = useTodoDispatch();

  const todosList = [
    {
      id: 1,
      content: "店予約する",
      editing: false,

    },
    {
      id: 2,
      content: "卵買う",
      editing: false,
    },
    {
      id: 3,
      content: "郵便出す",
      editing: false,
    },
  ];

  const [ todos, setTodos ] = useState(todosList);

  const deleteTodo = (id) => {
    const newTodos = state.filter((todo) => {
      return todo.id !== id;
    });

    dispatch(newTodos);
  }

  const createTodo = (todo) => {
    dispatch([...state, todo]);
  }

  const upDateTodo = (todo) => {
    const newTodo =  todosList.map(_todo => { 
