import {useReducer, useState} from "react";

// useReducerはuseStateの置き換えに使用
const Example = () => {
  const [state, setState] = useState(0);
  const [rstate, dispatch] = useReducer((prev, action) =>{
    switch(action){
      case'+':
        return ++prev;
      case'-':
        return --prev;
      default:
        throw new Error('不明なアクションです。')

    }

    //   if(action === '+'){
  //     return ++prev;
  //  } else if(action === '-'){
  //   return --prev;
  //  }
  }, 0);

  const countUp = () => {
    setState(prev => ++prev)
  }
  const rcountUp = () => {
    dispatch('+');
  }
  const rcountDown = () => {
    dispatch('-');
  }
  return (
    <>
      <div>
       <h3>{state}</h3>
       <button onClick={countUp}>+</button>
      </div>
      <div>
       <h3>{rstate}</h3>
       <button onClick={rcountUp}>+</button>
       <button onClick={rcountDown}>-</button>
      </div>
    </>
  );
};

export default Example;
