import {useTodo, useTodoDispatch } from "../context/TodoContext";


const Item =({todos, deleteTodo}) => {

    const state = useTodo();
    const dispatch = useTodoDispatch();

    const complete = (id) => {
        deleteTodo(id)
    }

    const doubleClickHandle =() =>{
        dispatch({ isEdit: true });
    }
return (

    state.map(todo => {
        return (
            <div key={todo.id}>
                <button onClick={() => complete(todo.id)}>完了</button>
                <span onDoubleClick={() => doubleClickHandle()}>
                    {todo.content}
                </span>
            </div>
        )})
)}

export default Item;
