import { useReducer } from "react";

const CALC_OPTIONS = ["add", "minus", "divide", "multiply"];

const reducer = (prev, { value }) => {
switch (value) {
  case "+":
    return prev + step;
  case "-":
    return prev - step;
  default:
    throw new Error('不明なactionです。')
}
}

const Example = () => {
  const initState = {
    a: 1,
    b: 2,
    result: 3,
  };

  const [state, dispatch] = useReducer(reducer, initState);

 

  const calculate = (e) => {
    dispatch(value);
  };

  const numChangeHandler = (e, {value}) => {
    console.log(e.target.value)
    console.log(initState.a)
    console.log(value)
    initState.a = e.target.value
    return initState.a
  };

  return (
    <>
    <h3>練習問題</h3>
    <p>useReducerを使って完成コードと同じ機能を作成してください。</p>
      <div>
        a:
        <input
          type="number"
          name="a"
          value={state.a}
          onChange={numChangeHandler}
        />
      </div>
      <div>
        b:
        <input
          type="number"
          name="b"
          value={state.b}
          onChange={numChangeHandler}
        />
      </div>
      <select value={state.type} onChange={calculate}>
        {CALC_OPTIONS.map(opt => <option key={opt} value={opt}>{opt}</option>)}
      </select>
      <h1>結果：{state.result}</h1>
    </>
  );
};

export default Example;
