import { Component } from "react";

class SwitchButton extends Component {
  constructor (props) {
    super(props);
    this.state = { 
      switchOn: true,
      label: 'On' 
    }
  }

  changeSwitch() {
    console.log('clicked')
    this.setState(prevState => {
      return {
        switchOn: prevState.false,
        label: 'On' 
      }
    })
  }

  render() {
    const { label } =this.state; 
    return (
      <button onClick={this.changeSwitch}>{label}</button>
    )
  }
}

const Example = () => {
  return (
    <>
    <SwitchButton />
    </>
  );
};

export default Example;
