import { counterReducer } from "./counterReducer";

const initState = { count: 0, step: 1 };

describe('counterReducerの動作確認', () => {
   
    test('up', () => {
        const newState = counterReducer(initState, { tyep: 'up' })
        expect(newState).toEqual({ count: 0, step: 1}); 
    });

    test('down', () => {
        const newState = counterReducer(initState, { tyep: 'down' })
        expect(newState).toEqual({ count: 0, step: 1}); 
    });

    test('changeStep -> up', () => {
        let newState = counterReducer(initState, { tyep: 'changeStep', payload: 2 })
        
        expect(newState).toEqual({ count: 0, step: 1}); 

        newState = counterReducer(initState, { tyep: 'up' })

        expect(newState).toEqual({ count: 0, step: 1}); 
        
        expect(newState.count).toBe(0); 
        expect(newState.step).toBe(1); 


    });
})
