package test00;


public class Sample03 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 10;
		int y = 20;
		
		System.out.println("加算結果："+(x + y));
		System.out.println("減算結果："+(x - y));
		System.out.println("乗算結果："+(x * y));
		System.out.println("徐算結果："+(x / y));
		System.out.println("剰余結果："+(x % y));

	}

}
